(function () {
    "use strict";
    const root = document.documentElement;
    const navToggle = document.querySelector("#js-navToggle");
    navToggle.addEventListener("click", function () {
    root.classList.toggle("show-nav");
    });

    const eventPP = document.querySelector("#js-eventPP");
    if (eventPP) {
    const eventOpenBtn = document.querySelector("#js-eventOpenBtn");
    const closeEventPP = function (event) {
      function close() {
        document.removeEventListener("keyup", closeEventPP);
        eventPP.removeEventListener("click", closeEventPP);
        root.classList.remove("show-event-popup");
      }
      switch (event.type) {
        case "keyup":
          if (event.key === "Escape" || event.keyCode === 27) close();
          break;
        case "click":
          if (
            event.target === this ||
            event.target.classList.contains("js-ppCloseBtn")
          )
            close();
          break;
      }
    };
    eventOpenBtn.addEventListener("click", function () {
      root.classList.add("show-event-popup");
      document.addEventListener("keyup", closeEventPP);
      eventPP.addEventListener("click", closeEventPP);
    });

    const sliderPics = document.querySelector('#js-sliderPics');
    const sliderRects = document.querySelectorAll('#js-sliderRect');
    const sliderLeftBtn = document.querySelector('#js-sliderLeftBtn');
    const sliderRightBtn = document.querySelector('#js-sliderRightBtn');
    const sliderPicWidth = 340;
    let currentIndex = 0;

    sliderPics.style.transform = `translateX(-${sliderPicWidth * 0}px)`;
    sliderRects[0].classList.add('events-block__slider-rect--active');

    sliderLeftBtn.addEventListener('click', () => {
      if (currentIndex === 0) {
        currentIndex = sliderRects.length - 1;
      } else {
        currentIndex--;
      }
      sliderPics.style.transform = `translateX(-${sliderPicWidth * currentIndex}px)`;
      setActiveRect();
    });

    sliderRightBtn.addEventListener('click', () => {
      if (currentIndex === sliderRects.length - 1) {
        currentIndex = 0;
      } else {
        currentIndex++;
      }
      sliderPics.style.transform = `translateX(-${sliderPicWidth * currentIndex}px)`;
      setActiveRect();
    });

    function setActiveRect() {
      sliderRects.forEach(rect => rect.classList.remove('events-block__slider-rect--active'));
      sliderRects[currentIndex].classList.add('events-block__slider-rect--active');
    }

  }
}
)();